(()=>{const e=499,n=function(){const e={quarters:25,dimes:10,nickels:5,pennies:1};function n(s,i){if(0===i)return 0;let r="";for(let n in e)if(e[n]<=i){r=n;break}return Math.floor(i/e[r])+n(s,i%e[r])}return{quarters:e=>n("quarters",e),dimes:e=>n("dimes",e),nickels:e=>n("nickels",e),pennies:e=>n("pennies",e)}}();console.log({quarters:n.quarters(e),dimes:n.dimes(e),nickels:n.nickels(e),pennies:n.pennies(e)})})();